import React, { useState } from "react";
import iconArrow from "../assets/images/icon-arrow.svg";

const BirthdayEntry = () => {
  const [day, setDay] = useState("");
  const [month, setMonth] = useState("");
  const [year, setYear] = useState("");
  const [error, setError] = useState("");

  const handleDayChange = (e) => {
    const inputDay = e.target.value;
    if (inputDay >= 1 && inputDay <= 28) {
      setDay(inputDay);
      setError("");
    } else {
      setError("Invalid day for the selected month.");
    }
  };

  const handleMonthChange = (e) => {
    const inputMonth = e.target.value;
    if (inputMonth >= 1 && inputMonth <= 12) {
      setMonth(inputMonth);
      setError("");
    } else {
      setError("Invalid month.");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (day && month && year) {
      // Your submission logic here
    } else {
      setError("Please fill in all fields.");
    }
  };

  return (
    <div className="birthday-entry-container">
      <form className="birthday-entry" onSubmit={handleSubmit}>
        <div className="birthday-inputs">
          <div className="day-input">
            <label>DAY</label>
            <input
              type="number"
              placeholder="DD"
              value={day}
              onChange={handleDayChange}
              min="1"
              max="28"
            />
          </div>
          <div className="month-input">
            <label>MONTH</label>
            <input
              type="number"
              placeholder="MM"
              value={month}
              onChange={handleMonthChange}
              min="1"
              max="12"
            />
          </div>
          <div className="year-input">
            <label>YEAR</label>
            <input
              type="text"
              placeholder="YYYY"
              value={year}
              onChange={(e) => setYear(e.target.value)}
            />
          </div>
        </div>
        {error && <div className="error-message">{error}</div>}
        <div className="button-container">
          <hr />
          <div className="submit-button">
            <img className="icon-arrow" src={iconArrow} alt="" />
          </div>
        </div>
      </form>
    </div>
  );
};

export default BirthdayEntry;
