import React from 'react';

const BirthdayResult = ({sharedMonth, sharedDay}) => {

  const cleanSharedDay = (sharedDay) => {
    // Remove leading '0' if present
    if (sharedDay.length > 1 && sharedDay[0] === '0') {
      return sharedDay.slice(1);
    }
    return sharedDay;
  };

  const cleanSharedMonth = (sharedMonth) => {
    // Remove leading '0' if present
    if (sharedMonth.length > 1 && sharedMonth[0] === '0') {
      const correctedMonth = parseInt(sharedMonth.slice(1))
      return correctedMonth;
    }
    return sharedMonth;
  };

  const birthday = new Date(1997, cleanSharedMonth(sharedMonth), cleanSharedDay(sharedDay)); // Change this to your actual birthday

  const currentDate = new Date();
  const timeDifference = currentDate - birthday;

  const years = Math.floor(timeDifference / (1000 * 60 * 60 * 24 * 365.25));
  const months = Math.floor((timeDifference % (1000 * 60 * 60 * 24 * 365.25)) / (1000 * 60 * 60 * 24 * 30.44));
  const days = Math.floor((timeDifference % (1000 * 60 * 60 * 24 * 30.44) / (1000 * 60 * 60 * 24)));

  return (
    <div className="birthday-result-container">
      <p className="years">{years} years</p>
      <p className="months">{months} months</p>
      <p className="days">{days} days</p>
    </div>
  );
};

export default BirthdayResult;
