import iconArrow from "../assets/images/icon-arrow.svg";
import { useState } from 'react';

const BirthdayEntry = ({updateCalendarValues}) => {
  const [formData, setFormData] = useState({
    month: "",
    day: "",
    year: ""
  })

  const [errors, setErrors] = useState({})

  const handleChange = (e) => {
    const {name, value} = e.target;
    setFormData({
      ...formData, [name] : value
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const validationErrors = {}

    const isValidDay = parseInt(formData.day) >= 1 && parseInt(formData.day) <= 31;
    const isValidMonth = parseInt(formData.month) >= 1 && parseInt(formData.month) <= 12;
    const isValidYear = parseInt(formData.year) >= 1 && parseInt(formData.year) <= 9999; 

    if(!isValidDay ) {
      validationErrors.day = "out of range"
    }
    if(!isValidMonth ) {
      validationErrors.month = "out of range"
    }
    if(!isValidYear ) {
      validationErrors.year = "out of range"
    }

    //february
    if(parseInt(formData.month) == 2 && parseInt(formData.day) > 28) {
      validationErrors.day = "out of range!!! YOU FUCK"
    }

    if(!formData.day.trim()) {
      validationErrors.day = "day is required"
    }
    if(!formData.month.trim()) {
      validationErrors.month = "month is required"
    }
    if(!formData.year.trim()) {
      validationErrors.year = "year is required"
    }

    setErrors(validationErrors)

    if (isValidDay && isValidMonth && isValidYear && Object.keys(validationErrors).length === 0) {
      updateCalendarValues(formData);
    }
  };

  return (
    <div className="birthday-entry-container">
      <form className="birthday-entry">
        <div className="birthday-inputs">
          <div className="day-input">
            <label>DAY</label>
            <input
              id="dayInput"
              name="day"
              type="number"
              placeholder="DD"
              min="1"
              max="31"
              onChange={handleChange} 
            />
            {errors.day && <span>{errors.day}</span>}

          </div>
          <div className="month-input">
            <label>MONTH</label>
            <input
              type="number"
              name="month"
              placeholder="MM"
              min="1"
              max="12"
              onChange={handleChange} 
            />
              {errors.month && <span>{errors.month}</span>}
          </div>
          <div className="year-input">
            <label>YEAR</label>
            <input 
              type="number" 
              name="year"
              placeholder="YYYY" 
              onChange={handleChange} 
             />
              {errors.year && <span>{errors.year}</span>}
          </div>
        </div>
        <div className="button-container">
          <hr />
          <div className="submit-button">
            <img onClick={handleSubmit} className="icon-arrow" src={iconArrow} alt="" />
          </div>
        </div>
      </form>
    </div>
  );
};

export default BirthdayEntry;
